import { call, put, takeEvery } from 'redux-saga/effects';
import { DashboardActionTypes, countUserSuccess } from './action';
import { showProgress, hideProgress } from '../AdminConsole/actions';
import { ConnectRepository } from '../../api/repositories/ConnectRepository';
import { addRequestFailedError } from './action';

export function* getHistoryJob(countUserAction) {

    const response = yield call(ConnectRepository.getUserCount, countryIds, permissionCountries, permissionEcosystems);
    if (response) {
        yield put(countUserSuccess(response));
    } else {
        yield put(addRequestFailedError(response));
    }
}

export function* watchHistoryActions() {
    yield takeEvery(HistoryActionTypes.GET_HISTORY, getHistoryJob);
}
